        setShowModal(true);

  }

  return (
    <Container fluid className="services-container">
        <Row>
            <Col>
            <h2>Core Services</h2>
            </Col>
        </Row>
        <Row>
            <Col>
                <Button variant="primary" onClick={() => handleServiceClick('Individual therapy is your chance to work one-on-one with me. I will support and empower individuals to regain control of their life by reaching their therapeutic goals.')}>
                Individual Counselling
                </Button>
                <Button variant="primary" onClick={() => handleServiceClick('Couples therapy is essential for any relationship that needs improving their communication, repairing trust, conflict resolution, enhancing emotional physical intimacy. Don’t allow relationship challenges to gain grounds any longer. I am here to help.')}>
                Couples Counselling
                </Button>
                <Button variant="primary" onClick={() => handleServiceClick('Family therapy provides a respectful milieu to help reduce family relational distress by ensuring a safe space for expressing feelings, bring cohesion and harmony, restore hope and strengthen the family relationships.')}>
                Family Counselling
                </Button>
                <Button variant="primary" onClick={() => handleServiceClick('Teen counselling can be very effective in helping teenagers deal with issues such as anxiety, trauma, grief, relational issues and other day-to-day struggles.')}>
                Teen Counselling
                </Button>
                <Button variant="primary" onClick={() => handleServiceClick('Grief, loss and trauma counselling is a significant and compassion form of therapy designed to nurture and come alongside you on your journey as you deal with loss and other related issues. I echo the sentiments of Shurland when he says: \"People need to feel heard and supported. My aim always is to walk with them through life’s moments, the pleasant and the hard ones\”')}>
                Grief, Loss and Trauma
                </Button>
                <Button variant="primary" onClick={() => handleServiceClick('In this group therapy, I offer a confidential, secure and safe space where you can express your feelings.')}>
                Group Therapy
                </Button>
            </Col>
        </Row>
        <Modal show={showModal} onHide={() => setShowModal(false)} centered>
            <Modal.Header closeButton>
                <Modal.Title>{selectedService?.name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {selectedService?.description}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={() => setShowModal(false)}>Close</Button>
            </Modal.Footer>
        </Modal>
    </Container>
  );
}

export default Services;
