import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import {Container, Row, Col} from "react-bootstrap";
import meter1 from "../assets/pics/meter1.svg";
import meter2 from "../assets/pics/meter2.svg";
import meter3 from "../assets/pics/meter3.svg";
import colorSharp from "../assets/pics/color-sharp.png";

export const Services = () => {
    const responsive = {
        superLargeDesktop: {
            breakpoint: {max: 4000, min: 3000},
            items: 5
        },
        desktop: {
            breakpoint: {max: 3000, min: 1024},
            items: 3
        },
        tablet: {
            breakpoint: {max: 1024, min: 464},
            items: 2
        },
        mobile: {
            breakpoint: {max: 464, min: 0},
            items: 1
        }
    };

    return(
        <section classname="service" id= "services">
            <Container>
                <Row>
                    <Col>
                    <div className="service-bx">
                        <h2>
                            Core Services
                        </h2>
                        <p>Individual therapy is your chance to work one-on-one with me. I will support and empower individuals to regain control of their life by reaching their therapeutic goals.</p>
                        <Carousel responsive={responsive} infinite={true} className="service-slider">
                            <div className="item">
                                <img src={meter1} alt="Image" />
                                <h5>Individual Therapy</h5>
                            </div>
                            <div className="item">
                                <img src={meter2} alt="Image" />
                                <h5>Group Therapy</h5>
                            </div>
                            <div className="item">
                                <img src={meter3} alt="Image" />
                                <h5>Family Therapy</h5>
                            </div>
                            <div className="item">
                                <img src={meter1} alt="Image" />
                                <h5>Couples Therapy</h5>
                            </div>
                            <div className="item">
                                <img src={meter2} alt="Image" />
                                <h5>Grief and Loss</h5>
                            </div>
                            <div className="item">
                                <img src={meter3} alt="Image" />
                                <h5>Teens Counselling</h5>
                            </div>
                        </Carousel>
                    </div>
                    </Col>
                </Row>
            </Container>
            <img className="background-image-left" src={colorSharp} />
        </section>
    )
}
